#!/bin/sh
# Name: Telnet
# Author: Foskya
# Icon: data:image/png;base64,


screen_refresh() {
    eips -c > /dev/null 2>&1
    eips -c > /dev/null 2>&1
}

start_telnet() {
    IP_ADDR=$(ifconfig | 
        grep -Eo 'inet (addr:)?([0-9]*\.){3}[0-9]*' | 
        grep -Eo '([0-9]*\.){3}[0-9]*' | 
        grep -v '127.0.0.1')
    killall telnetd &> /dev/null
    echo "Telnet Started On: $IP_ADDR : 23"
    if ! iptables -C INPUT -p tcp --dport 23 -j ACCEPT 2>/dev/null; then
        iptables -A INPUT -p tcp --dport 23 -j ACCEPT
    fi
    telnetd -l /bin/sh -p 23
}

kill_telnet() {
    killall telnetd &> /dev/null
    echo " Telnet Stopped! "
    sleep 2
    screen_refresh
}

detect_touch_device() {
    awk '{ 
        if ($1 == "Section" && $2 == "\"InputDevice\"") { isInput=1 }; 
        if ($2 == "\"Device\"" && isInput) { inputDevice=$3; hasInputDevice=1 }; 
        if ($2 == "\"CorePointer\"" && hasInputDevice && isInput) { 
            gsub(/\"/, "", inputDevice); 
            print inputDevice 
        } 
    }' /etc/xorg.conf
}

TOUCH_DEVICE=$(detect_touch_device)
wait_for_touch() {
    if [ -z "$TOUCH_DEVICE" ]; then
        echo "Error: Could not detect touch device!"
        kill_telnet
        sleep 2
        exit 1
    fi

    while :; do
        dd if="$TOUCH_DEVICE" bs=16 count=1 2>/dev/null | grep -q .
        if [ $? -eq 0 ]; then
            break
        fi
        sleep 1  # Reduce CPU usage
    done
}

# MAIN
screen_refresh
echo "
 _____    _            _   
|_   _|  | |          | |  
  | | ___| |_ __   ___| |_ 
  | |/ _ \ | '_ \ / _ \ __|
  | |  __/ | | | |  __/ |_ 
  \_/\___|_|_| |_|\___|\__|


"
start_telnet
echo " "
echo " "
echo "Touch the screen to stop"
wait_for_touch
screen_refresh
kill_telnet